import React, { useState, useEffect } from 'react';
import { Link, useSearchParams } from 'react-router-dom';
import localProductService from '../services/localProductService';
import WishlistButton from '../components/WishlistButton';

const Products = () => {
  const [searchParams] = useSearchParams();
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    category: searchParams.get('category') || '',
    filter: searchParams.get('filter') || '',
    search: searchParams.get('search') || '',
    priceRange: 'all',
    shoeType: '',
    gender: ''
  });

  useEffect(() => {
    const loadProducts = async () => {
      setLoading(true);
      try {
        const localProducts = await localProductService.getProducts();
        setProducts(localProducts);
      } catch (error) {
        console.error('Error loading products:', error);
      } finally {
        setLoading(false);
      }
    };
    loadProducts();
  }, []);

  useEffect(() => {
    setFilters(prev => ({
      ...prev,
      search: searchParams.get('search') || '',
      category: searchParams.get('category') || '',
      filter: searchParams.get('filter') || ''
    }));
  }, [searchParams]);

  useEffect(() => {
    let filtered = products;

    if (filters.category) {
      if (filters.category === 'clothes') {
        filtered = filtered.filter(product => 
          product.category === 'women' || product.category === 'men' || product.category === 'kid'
        );
      } else {
        filtered = filtered.filter(product => product.category === filters.category);
      }
    }

    if (filters.category === 'clothes' && filters.gender) {
      filtered = filtered.filter(product => product.category === filters.gender);
    }

    if (filters.category === 'shoes' && filters.shoeType) {
      filtered = filtered.filter(product => 
        product.subcategory && product.subcategory === filters.shoeType
      );
    }

    if (filters.filter) {
      filtered = filtered.filter(product => product.tags.includes(filters.filter));
    }

    if (filters.search) {
      const searchLower = filters.search.toLowerCase();
      filtered = filtered.filter(product => 
        product.name.toLowerCase().includes(searchLower) ||
        product.description?.toLowerCase().includes(searchLower) ||
        product.category.toLowerCase().includes(searchLower) ||
        product.subcategory?.toLowerCase().includes(searchLower) ||
        product.brand?.toLowerCase().includes(searchLower) ||
        product.tags.some(tag => tag.toLowerCase().includes(searchLower))
      );
    }

    if (filters.priceRange !== 'all') {
      switch (filters.priceRange) {
        case 'under50':
          filtered = filtered.filter(product => product.price < 50);
          break;
        case '50to100':
          filtered = filtered.filter(product => product.price >= 50 && product.price <= 100);
          break;
        case '100to200':
          filtered = filtered.filter(product => product.price >= 100 && product.price <= 200);
          break;
        case 'over200':
          filtered = filtered.filter(product => product.price > 200);
          break;
        default:
          break;
      }
    }

    setFilteredProducts(filtered);
  }, [products, filters]);

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({ ...prev, [key]: value }));
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-black mx-auto mb-4"></div>
          <p className="text-gray-600">Loading products...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="mb-8">
          <h1 className="text-4xl font-light text-black mb-4">
            {filters.search 
              ? `Search Results for "${filters.search}"`
              : filters.category 
                ? `${filters.category.charAt(0).toUpperCase() + filters.category.slice(1)}` 
                : 'All Products'
            }
          </h1>
          <p className="text-gray-600">
            {filteredProducts.length} products found
            {filters.search && (
              <span className="ml-2">
                â€¢ <button 
                    onClick={() => setFilters(prev => ({ ...prev, search: '' }))} 
                    className="text-blue-600 hover:underline"
                  >
                    Clear search
                  </button>
              </span>
            )}
          </p>
        </div>
      </div>
    </div>
  );
};

export default Products;
