import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import localProductService from '../services/localProductService';

const Home = () => {
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentShoe, setCurrentShoe] = useState(0);

  // Shoe data from trial folder
  const shoes = [
    {
      id: 1,
      name: "NIKE AIR ROVER",
      image: "/images/shoes/NIKE+AVA+ROVER.avif",
      price: 749,
      brand: "Nike"
    },
    {
      id: 2, 
      name: "OZWEEGO",
      image: "/images/shoes/OZWEEGO_Shoes_Black_EE6999_01_00_standard.avif",
      price: 749,
      brand: "Adidas"
    },
    {
      id: 3,
      name: "PUMA CLASSIC",
      image: "/images/shoes/puma.jpg", 
      price: 749,
      brand: "Puma"
    }
  ];

  // Auto-rotate shoes every 4 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentShoe((prev) => (prev + 1) % shoes.length);
    }, 4000);
    return () => clearInterval(interval);
  }, [shoes.length]);

  // Fetch featured products from local service
  useEffect(() => {
    const fetchFeaturedProducts = async () => {
      try {
        const products = await localProductService.getFeaturedProducts(4);
        setFeaturedProducts(products);
      } catch (error) {
        console.error('Error fetching featured products:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchFeaturedProducts();
  }, []);

  return (
    <div className="min-h-screen">
      {/* Inline CSS for animations */}
      <style>{`
        @keyframes floatUpDown {
          0%, 100% { 
            transform: translateY(0px) rotate(12deg) scale(1); 
          }
          50% { 
            transform: translateY(-25px) rotate(8deg) scale(1.05); 
          }
        }
        @keyframes shimmerEffect {
          0%, 100% { 
            opacity: 0.6; 
            transform: scale(1) rotate(0deg); 
          }
          50% { 
            opacity: 0.9; 
            transform: scale(1.1) rotate(5deg); 
          }
        }
        @keyframes glowPulse {
          0% { 
            opacity: 0.7; 
            box-shadow: 0 0 20px rgba(34, 211, 238, 0.4); 
          }
          100% { 
            opacity: 1; 
            box-shadow: 0 0 30px rgba(34, 211, 238, 0.8); 
          }
        }
        @keyframes soleFloat {
          0%, 100% { 
            transform: translateX(-50%) translateY(0px) scaleX(1); 
          }
          50% { 
            transform: translateX(-50%) translateY(-8px) scaleX(1.1); 
          }
        }
        .shoe-container {
          animation: floatUpDown 6s ease-in-out infinite;
        }
        .shimmer-1 {
          animation: shimmerEffect 3s ease-in-out infinite;
        }
        .shimmer-2 {
          animation: shimmerEffect 3s ease-in-out infinite 1.5s;
        }
        .glow-swoosh {
          animation: glowPulse 2s ease-in-out infinite alternate;
        }
        .sole-float {
          animation: soleFloat 6s ease-in-out infinite;
        }
      `}</style>
      
      {/* Animated Floating Shoe Hero Section */}
      <section className="relative h-screen overflow-hidden">
        {/* Gradient Background */}
        <div className="absolute inset-0 bg-gradient-to-br from-purple-900 via-blue-800 to-cyan-400"></div>
        
        {/* Animated Background Elements */}
        <div className="absolute inset-0">
          <div className="absolute top-20 left-10 w-2 h-2 bg-white rounded-full opacity-60 animate-pulse"></div>
          <div className="absolute top-40 right-20 w-1 h-1 bg-white rounded-full opacity-40 animate-bounce"></div>
          <div className="absolute bottom-32 left-1/4 w-1.5 h-1.5 bg-cyan-300 rounded-full opacity-50 animate-ping"></div>
          <div className="absolute top-1/3 right-1/3 w-1 h-1 bg-purple-300 rounded-full opacity-60 animate-pulse"></div>
        </div>

        {/* Main Content Container */}
        <div className="relative z-20 h-full flex items-center justify-between">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 w-full grid grid-cols-1 lg:grid-cols-2 items-center">
            
            {/* Left Side - Dynamic Text Content */}
            <div className="text-white space-y-6">
              <div className="space-y-2">
                <h1 className="text-7xl lg:text-9xl font-bold tracking-wider">
                  <span className="block">{shoes[currentShoe].name}</span>
                </h1>
                <div className="flex items-center space-x-1 text-yellow-400">
                  {[...Array(5)].map((_, i) => (
                    <svg key={i} className="w-4 h-4 fill-current" viewBox="0 0 20 20">
                      <path d="M10 15l-5.878 3.09 1.123-6.545L.489 6.91l6.572-.955L10 0l2.939 5.955 6.572.955-4.756 4.635 1.123 6.545z"/>
                    </svg>
                  ))}
                </div>
              </div>
              
              <div className="space-y-4">
                <div className="text-4xl font-bold">
                  ${shoes[currentShoe].price}
                </div>
                <p className="text-lg opacity-80 max-w-md">
                  Experience premium comfort and style with the {shoes[currentShoe].brand} {shoes[currentShoe].name}. 
                  Engineered for performance and designed for the streets.
                </p>
                
                <button className="bg-white text-black px-8 py-3 font-semibold text-sm tracking-wider hover:bg-gray-100 transition-all duration-300 transform hover:scale-105">
                  BUY NOW
                </button>
              </div>
            </div>

            {/* Right Side - Real Shoe Images with Animation */}
            <div className="relative flex justify-center items-center h-full">
              {/* Floating Particles Background */}
              <div className="absolute inset-0">
                <div className="absolute top-1/4 left-1/4 w-1 h-1 bg-cyan-300 rounded-full animate-ping opacity-60"></div>
                <div className="absolute top-1/3 right-1/4 w-2 h-2 bg-purple-300 rounded-full animate-bounce opacity-50"></div>
                <div className="absolute bottom-1/3 left-1/3 w-1.5 h-1.5 bg-white rounded-full animate-pulse opacity-70"></div>
                <div className="absolute top-1/2 right-1/2 w-1 h-1 bg-pink-300 rounded-full animate-ping opacity-40"></div>
                <div className="absolute bottom-1/4 right-1/3 w-2 h-2 bg-blue-300 rounded-full animate-bounce opacity-60"></div>
              </div>

              {/* Main Shoe Container */}
              <div className="relative">
                {/* Glow Effect Behind Shoe */}
                <div className="absolute inset-0 bg-gradient-to-r from-cyan-400/30 via-purple-500/20 to-transparent rounded-full blur-3xl animate-pulse" style={{width: '500px', height: '500px'}}></div>
                
                {/* Real Shoe Image with Animations */}
                <div className="relative w-96 h-96 flex items-center justify-center">
                  <div className="relative z-10 transform transition-all duration-700 hover:rotate-6 shoe-container">
                    {/* Actual Shoe Image */}
                    <div className="relative w-80 h-80 flex items-center justify-center">
                      <img
                        src={shoes[currentShoe].image}
                        alt={shoes[currentShoe].name}
                        className="w-full h-full object-contain transform rotate-12 transition-all duration-1000"
                        style={{
                          filter: 'drop-shadow(0 25px 50px rgba(0,0,0,0.5)) drop-shadow(0 0 50px rgba(34, 211, 238, 0.3))',
                          maxWidth: '320px',
                          maxHeight: '320px'
                        }}
                        onError={(e) => {
                          console.log('Image failed to load:', e.target.src);
                          e.target.style.display = 'none';
                          e.target.nextSibling.style.display = 'flex';
                        }}
                      />
                      {/* Fallback if image doesn't load */}
                      <div className="hidden w-full h-full items-center justify-center text-white bg-gray-800 rounded-lg">
                        <div className="text-center">
                          <p className="text-lg">{shoes[currentShoe].name}</p>
                          <p className="text-sm opacity-60">Image Loading...</p>
                        </div>
                      </div>
                      
                      {/* Enhanced Glow Effects Around Image */}
                      <div className="absolute inset-0 pointer-events-none">
                        <div 
                          className="absolute top-1/4 left-1/4 w-16 h-16 rounded-full blur-xl shimmer-1"
                          style={{
                            background: 'radial-gradient(circle, rgba(34, 211, 238, 0.6) 0%, transparent 70%)'
                          }}
                        ></div>
                        <div 
                          className="absolute bottom-1/4 right-1/4 w-12 h-12 rounded-full blur-xl shimmer-2"
                          style={{
                            background: 'radial-gradient(circle, rgba(168, 85, 247, 0.6) 0%, transparent 70%)'
                          }}
                        ></div>
                      </div>
                      
                      {/* Floating Reflection/Shadow */}
                      <div 
                        className="absolute bottom-0 left-1/2 transform -translate-x-1/2 sole-float"
                        style={{
                          width: '280px',
                          height: '40px',
                          background: 'radial-gradient(ellipse, rgba(255,255,255,0.3) 0%, rgba(34, 211, 238, 0.2) 30%, transparent 70%)',
                          borderRadius: '50%',
                          filter: 'blur(8px)',
                          opacity: '0.6'
                        }}
                      ></div>
                    </div>
                    
                    {/* Enhanced Floating Sparkles */}
                    <div className="absolute -top-8 -right-8 w-4 h-4 bg-cyan-400 rounded-full animate-bounce opacity-80" style={{boxShadow: '0 0 20px rgba(34, 211, 238, 0.5)'}}></div>
                    <div className="absolute -bottom-6 -left-6 w-3 h-3 bg-purple-400 rounded-full animate-pulse opacity-70" style={{boxShadow: '0 0 15px rgba(168, 85, 247, 0.5)'}}></div>
                    <div className="absolute top-1/2 -left-12 w-2 h-2 bg-white rounded-full animate-ping opacity-80"></div>
                    <div className="absolute top-1/4 -right-10 w-2 h-2 bg-pink-400 rounded-full animate-bounce opacity-60" style={{animationDelay: '1s'}}></div>
                    <div className="absolute bottom-1/4 right-12 w-1 h-1 bg-yellow-400 rounded-full animate-pulse opacity-70" style={{animationDelay: '2s'}}></div>
                  </div>
                </div>
              </div>

              {/* Interactive Navigation Arrows */}
              <button 
                onClick={() => setCurrentShoe((prev) => prev === 0 ? shoes.length - 1 : prev - 1)}
                className="absolute left-4 top-1/2 transform -translate-y-1/2 w-12 h-12 border-2 border-white/30 rounded-full flex items-center justify-center text-white hover:bg-white/10 transition-all z-30"
              >
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                </svg>
              </button>
              
              <button 
                onClick={() => setCurrentShoe((prev) => (prev + 1) % shoes.length)}
                className="absolute right-4 top-1/2 transform -translate-y-1/2 w-12 h-12 border-2 border-white/30 rounded-full flex items-center justify-center text-white hover:bg-white/10 transition-all z-30"
              >
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                </svg>
              </button>
            </div>
          </div>
        </div>

        {/* Interactive Bottom Indicators */}
        <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 flex space-x-2 z-30">
          {shoes.map((_, index) => (
            <button
              key={index}
              onClick={() => setCurrentShoe(index)}
              className={`h-2 rounded-full transition-all duration-300 ${
                index === currentShoe 
                  ? 'w-8 bg-white' 
                  : 'w-2 bg-white opacity-60 hover:opacity-80'
              }`}
            />
          ))}
        </div>

        {/* Floating Chat Support */}
        <div className="absolute bottom-8 left-8 z-30">
          <div className="bg-white/10 backdrop-blur-sm text-white p-3 rounded-full shadow-lg hover:bg-white/20 transition-colors cursor-pointer border border-white/20">
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 12h.01M12 12h.01M16 12h.01M21 12c0 4.418-4.03 8-9 8a9.863 9.863 0 01-4.255-.949L3 20l1.395-3.72C3.512 15.042 3 13.574 3 12c0-4.418 4.03-8 9-8s9 3.582 9 8z" />
            </svg>
          </div>
        </div>
      </section>

      {/* Featured Products Section */}
      <section className="py-20 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-16">
            <h2 className="text-4xl font-light text-black mb-4">Featured Products</h2>
            <p className="text-gray-600 text-lg">Discover our handpicked selection</p>
          </div>
          
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
            {loading ? (
              // Loading placeholders
              [1, 2, 3, 4].map((item) => (
                <div key={item} className="product-card">
                  <div className="aspect-square bg-gray-200 mb-4 flex items-center justify-center animate-pulse">
                    <span className="text-gray-400">Loading...</span>
                  </div>
                  <div className="h-4 bg-gray-200 rounded mb-2 animate-pulse"></div>
                  <div className="h-4 bg-gray-200 rounded w-3/4 mb-2 animate-pulse"></div>
                  <div className="h-6 bg-gray-200 rounded w-1/2 animate-pulse"></div>
                </div>
              ))
            ) : (
              // Real products from API
              featuredProducts.map((product) => (
                <div key={product.id} className="product-card group">
                  <Link to={`/products/${product.id}`}>
                    <div className="aspect-square bg-gray-200 mb-4 flex items-center justify-center overflow-hidden rounded-lg">
                      <img 
                        src={product.image} 
                        alt={product.name}
                        className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-300"
                        onError={(e) => {
                          e.target.style.display = 'none';
                          e.target.nextSibling.style.display = 'flex';
                        }}
                      />
                      <div className="hidden w-full h-full items-center justify-center text-gray-500 text-center p-4">
                        <span className="text-sm">{product.name}</span>
                      </div>
                    </div>
                    <div className="p-2">
                      <h3 className="text-lg font-medium text-black mb-2 group-hover:text-gray-700 transition-colors line-clamp-2">
                        {product.name}
                      </h3>
                      <p className="text-gray-600 mb-2 capitalize">{product.category}</p>
                      <div className="flex items-center justify-between">
                        <p className="text-xl font-semibold text-black">${product.price}</p>
                        <div className="flex items-center text-yellow-400">
                          <svg className="w-4 h-4 fill-current" viewBox="0 0 20 20">
                            <path d="M10 15l-5.878 3.09 1.123-6.545L.489 6.91l6.572-.955L10 0l2.939 5.955 6.572.955-4.756 4.635 1.123 6.545z"/>
                          </svg>
                          <span className="text-sm text-gray-600 ml-1">
                            {typeof product.rating === 'object' ? product.rating.average : product.rating}
                          </span>
                        </div>
                      </div>
                    </div>
                  </Link>
                  <div className="p-2 pt-0">
                    <button className="w-full btn-primary text-sm py-2">Add to Cart</button>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      </section>

      {/* Categories Section */}
      <section className="py-20 bg-gray-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-16">
            <h2 className="text-4xl font-light text-black mb-4">Shop by Category</h2>
            <p className="text-gray-600 text-lg">Find exactly what you're looking for</p>
          </div>
          
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            {[
              { name: 'Clothes', image: 'clothes-placeholder.jpg' },
              { name: 'Shoes', image: 'shoes-placeholder.jpg' }
            ].map((category) => (
              <Link 
                key={category.name}
                to={`/${category.name.toLowerCase()}`}
                className="group relative h-80 bg-gray-300 overflow-hidden flex items-center justify-center hover:scale-105 transition-transform duration-300"
              >
                <div className="absolute inset-0 bg-black bg-opacity-40 group-hover:bg-opacity-50 transition-all duration-300"></div>
                <div className="relative z-10 text-center text-white">
                  <h3 className="text-3xl font-light mb-2">{category.name}</h3>
                  <p className="text-sm tracking-wider">SHOP NOW</p>
                </div>
                <span className="absolute inset-0 flex items-center justify-center text-gray-500 text-lg">
                  {category.name} Image Placeholder
                </span>
              </Link>
            ))}
          </div>
        </div>
      </section>

      {/* Newsletter Section */}
      <section className="py-20 bg-black text-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <h2 className="text-4xl font-light mb-4">Stay Updated</h2>
          <p className="text-gray-300 text-lg mb-8">Get the latest news about our collections and exclusive offers</p>
          <div className="max-w-md mx-auto flex">
            <input 
              type="email" 
              placeholder="Enter your email"
              className="flex-1 px-4 py-3 text-black focus:outline-none focus:ring-2 focus:ring-white"
            />
            <button className="bg-white text-black px-6 py-3 font-medium hover:bg-gray-200 transition-colors">
              Subscribe
            </button>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
