import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import localProductService from '../services/localProductService';

const ClothesPage = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [genderFilter, setGenderFilter] = useState('');

  useEffect(() => {
    const loadClothesProducts = async () => {
      setLoading(true);
      try {
        const allProducts = await localProductService.getAllProducts();
        // Filter for clothing categories only (women, men, kid)
        const clothingProducts = allProducts.filter(product => 
          product.category === 'women' || product.category === 'men' || product.category === 'kid'
        );
        setProducts(clothingProducts);
        setFilteredProducts(clothingProducts);
      } catch (error) {
        console.error('Error loading clothes:', error);
      } finally {
        setLoading(false);
      }
    };

    loadClothesProducts();
  }, []);

  useEffect(() => {
    if (genderFilter) {
      setFilteredProducts(products.filter(product => product.category === genderFilter));
    } else {
      setFilteredProducts(products);
    }
  }, [genderFilter, products]);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-black mx-auto mb-4"></div>
          <p className="text-gray-600">Loading clothes...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Page Header */}
        <div className="mb-8">
          <h1 className="text-4xl font-light text-black mb-4">Clothes Collection</h1>
          <p className="text-gray-600 mb-6">
            {filteredProducts.length} clothing items available
          </p>
          
          {/* Gender Filter */}
          <div className="mb-6">
            <label className="block text-sm font-medium text-gray-700 mb-2">Filter by Gender</label>
            <select
              value={genderFilter}
              onChange={(e) => setGenderFilter(e.target.value)}
              className="px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-black focus:border-transparent"
            >
              <option value="">All Genders</option>
              <option value="women">Women</option>
              <option value="men">Men</option>
              <option value="kid">Kids</option>
            </select>
          </div>
        </div>

        {/* Products Grid */}
        {filteredProducts.length === 0 ? (
          <div className="text-center py-12">
            <p className="text-gray-600 text-lg">No clothes found.</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
            {filteredProducts.map((product) => (
              <div key={product.id} className="product-card group">
                <Link to={`/products/${product.id}`}>
                  <div className="aspect-square bg-gray-200 mb-4 flex items-center justify-center overflow-hidden rounded-lg">
                    <img
                      src={product.image}
                      alt={product.name}
                      className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-300"
                      onError={(e) => {
                        e.target.style.display = 'none';
                        e.target.nextSibling.style.display = 'flex';
                      }}
                    />
                    <div className="hidden w-full h-full items-center justify-center text-gray-500">
                      Image not found
                    </div>
                  </div>
                  <div className="p-4">
                    <h3 className="text-lg font-medium text-black mb-2 line-clamp-2">
                      {product.name}
                    </h3>
                    <p className="text-gray-600 mb-2 capitalize">{product.category}</p>
                    <div className="flex items-center justify-between">
                      <p className="text-xl font-semibold text-black">${product.price}</p>
                      <div className="flex items-center text-yellow-400">
                        <svg className="w-4 h-4 fill-current" viewBox="0 0 20 20">
                          <path d="M10 15l-5.878 3.09 1.123-6.545L.489 6.91l6.572-.955L10 0l2.939 5.955 6.572.955-4.756 4.635 1.123 6.545z"/>
                        </svg>
                        <span className="text-sm text-gray-600 ml-1">
                          {typeof product.rating === 'object' ? product.rating.average : product.rating}
                        </span>
                      </div>
                    </div>
                  </div>
                </Link>
                <div className="p-2 pt-0">
                  <button className="w-full btn-primary text-sm py-2">Add to Cart</button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default ClothesPage;
