import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import localProductService from '../services/localProductService';
import { useCart } from '../context/CartContext';
import WishlistButton from '../components/WishlistButton';
import CompareButton from '../components/CompareButton';
import StarRating from '../components/StarRating';
import { useReviews } from '../context/ReviewsContext';

const Home = () => {
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentShoe, setCurrentShoe] = useState(0);
  const { addItem } = useCart();
  const { getProductRating } = useReviews();

  // Shoe data from trial folder - Nike Speed Rep Style
  const shoes = [
    {
      id: 29,
      name: "NIKE AIR JORDAN 1",
      image: "/images/shoes/nike-jordan.webp",
      price: 749,
      brand: "Nike"
    },
    {
      id: 30, 
      name: "ADIDAS UNVEIL",
      image: "/images/shoes/adidas-unveil.jpg",
      price: 749,
      brand: "Adidas"
    },
    {
      id: 31,
      name: "PUMA DOVE",
      image: "/images/shoes/puma-dove.jpg", 
      price: 749,
      brand: "Puma"
    }
  ];

  // Handle adding current shoe to cart
  const handleAddToCart = () => {
    console.log('Add to cart clicked'); // Debug log
    const currentShoeData = shoes[currentShoe];
    console.log('Current shoe data:', currentShoeData); // Debug log
    
    try {
      addItem({
        id: currentShoeData.id,
        name: currentShoeData.name,
        price: currentShoeData.price,
        image: currentShoeData.image,
        size: "9", // Default size
        color: "#000000", // Default color
        quantity: 1
      });
      console.log('Item added to cart successfully'); // Debug log
      
      // Show success feedback
      const button = document.querySelector('.shop-now-btn');
      const originalText = button.textContent;
      button.textContent = 'Added to Cart!';
      button.classList.add('bg-green-500');
      button.classList.remove('bg-yellow-400');
      
      setTimeout(() => {
        button.textContent = originalText;
        button.classList.remove('bg-green-500');
        button.classList.add('bg-yellow-400');
      }, 2000);
      
    } catch (error) {
      console.error('Error adding to cart:', error);
      alert('Error adding item to cart. Please try again.');
    }
  };

  // Auto-rotate shoes every 4 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentShoe((prev) => (prev + 1) % shoes.length);
    }, 4000);
    return () => clearInterval(interval);
  }, [shoes.length]);

  // Fetch featured products from local service
  useEffect(() => {
    const fetchFeaturedProducts = async () => {
      try {
        const products = await localProductService.getFeaturedProducts(4);
        setFeaturedProducts(products);
      } catch (error) {
        console.error('Error fetching featured products:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchFeaturedProducts();
  }, []);

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Nike Speed Rep Style Hero Section */}
      <section className="relative min-h-screen overflow-hidden bg-gray-50">
        
        {/* Top Navigation Pills */}
        <div className="absolute top-4 md:top-8 left-1/2 transform -translate-x-1/2 flex space-x-1 text-sm z-30">
          <button 
            onClick={() => setCurrentShoe((prev) => prev === 0 ? shoes.length - 1 : prev - 1)}
            className="px-2 py-1 text-gray-600 hover:text-black transition-colors"
          >
            ←
          </button>
          <span className="px-3 md:px-4 py-1 bg-black text-white text-xs font-medium uppercase tracking-wider">
            {shoes[currentShoe].brand} TRAINING SHOE
          </span>
          <button 
            onClick={() => setCurrentShoe((prev) => (prev + 1) % shoes.length)}
            className="px-2 py-1 text-gray-600 hover:text-black transition-colors"
          >
            →
          </button>
        </div>

        {/* Main Content Container */}
        <div className="relative h-screen flex items-center justify-center pt-16 md:pt-20">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 w-full">
            
            {/* Large Product Name Typography - Single Line */}
            <div className="text-center mb-8 md:mb-12">
              <h1 className="text-3xl sm:text-4xl md:text-5xl lg:text-6xl xl:text-7xl font-light tracking-tight text-gray-900 leading-tight whitespace-nowrap">
                {shoes[currentShoe].name}
                <span className="inline-block ml-2 md:ml-4 w-8 md:w-16 h-0.5 md:h-1 bg-gray-400 align-middle"></span>
              </h1>
            </div>

            {/* Shoe Display Area */}
            <div className="relative flex justify-center items-center mb-8 md:mb-12">
              {/* Main Shoe Image */}
              <div className="relative">
                <img
                  src={shoes[currentShoe].image}
                  alt={shoes[currentShoe].name}
                  className="w-64 sm:w-80 md:w-96 lg:w-[500px] h-auto object-contain transform transition-all duration-1000 hover:scale-105"
                  style={{
                    filter: 'drop-shadow(0 20px 40px rgba(0,0,0,0.15))',
                    animation: 'gentleFloat 6s ease-in-out infinite'
                  }}
                  onError={(e) => {
                    console.log('Image failed to load:', e.target.src);
                    e.target.style.display = 'none';
                    e.target.nextSibling.style.display = 'flex';
                  }}
                />
                {/* Fallback */}
                <div className="hidden w-64 sm:w-80 md:w-96 h-64 sm:h-80 md:h-96 items-center justify-center text-gray-500 bg-gray-200 rounded-lg">
                  <div className="text-center">
                    <p className="text-lg">{shoes[currentShoe].name}</p>
                    <p className="text-sm opacity-60">Image Loading...</p>
                  </div>
                </div>

                {/* Floating Shadow Shoe (Background) - Hidden on mobile */}
                <div className="hidden lg:block absolute top-0 right-[-200px] xl:right-[-300px] opacity-20 pointer-events-none">
                  <img
                    src={shoes[currentShoe].image}
                    alt=""
                    className="w-64 xl:w-80 h-auto object-contain transform rotate-12 scale-150"
                    style={{
                      filter: 'blur(1px)',
                      animation: 'floatShadow 8s ease-in-out infinite'
                    }}
                  />
                </div>
              </div>
            </div>

            {/* Product Description - Responsive Layout */}
            <div className="flex flex-col md:flex-row justify-between items-center md:items-end max-w-4xl mx-auto space-y-6 md:space-y-0">
              <div className="text-center md:text-left">
                <p className="text-base md:text-lg text-gray-600 font-light mb-4 max-w-xs mx-auto md:mx-0">
                  When the shoes get lighter,<br />
                  the moves get tighter.
                </p>
                <button 
                  onClick={handleAddToCart}
                  className="shop-now-btn bg-yellow-400 text-black px-6 md:px-8 py-2 md:py-3 font-medium text-sm hover:bg-yellow-500 transition-colors"
                >
                  Shop now
                </button>
              </div>
              
              <div className="text-center md:text-right">
                <div className="text-xl md:text-2xl font-light text-gray-900 mb-2">
                  ${shoes[currentShoe].price}
                </div>
                <p className="text-xs text-gray-500 uppercase tracking-wider max-w-xs">
                  FREE DELIVERY | Applies to orders of $200 or more. 
                  <Link 
                    to={`/products/${shoes[currentShoe].id}`} 
                    className="underline hover:text-gray-700 ml-1"
                  >
                    View details
                  </Link>
                </p>
              </div>
            </div>

            {/* Bottom Shoe Indicators */}
            <div className="absolute bottom-16 md:bottom-8 left-1/2 transform -translate-x-1/2 flex space-x-2">
              {shoes.map((_, index) => (
                <button
                  key={index}
                  onClick={() => setCurrentShoe(index)}
                  className={`w-2 h-2 rounded-full transition-all duration-300 ${
                    index === currentShoe 
                      ? 'bg-gray-900' 
                      : 'bg-gray-400 hover:bg-gray-600'
                  }`}
                />
              ))}
            </div>
          </div>
        </div>

        {/* Enhanced CSS Animations */}
        <style>{`
          @keyframes gentleFloat {
            0%, 100% { 
              transform: translateY(0px) scale(1);
            }
            50% { 
              transform: translateY(-10px) scale(1.02);
            }
          }
          @keyframes floatShadow {
            0%, 100% { 
              transform: rotate(12deg) scale(150%) translateY(0px);
              opacity: 0.15;
            }
            50% { 
              transform: rotate(15deg) scale(155%) translateY(-15px);
              opacity: 0.25;
            }
          }
          .shoe-name-animation {
            animation: fadeInUp 1.2s ease-out forwards;
          }
          @keyframes fadeInUp {
            from {
              opacity: 0;
              transform: translateY(30px);
            }
            to {
              opacity: 1;
              transform: translateY(0);
            }
          }
        `}</style>
      </section>

      {/* Featured Products Section */}
      <section className="py-20 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-16">
            <h2 className="text-4xl font-light text-black mb-4">Featured Products</h2>
            <p className="text-gray-600 text-lg">Discover our handpicked selection</p>
          </div>
          
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
            {loading ? (
              // Loading placeholders
              [1, 2, 3, 4].map((item) => (
                <div key={item} className="product-card">
                  <div className="aspect-square bg-gray-200 mb-4 flex items-center justify-center animate-pulse">
                    <span className="text-gray-400">Loading...</span>
                  </div>
                  <div className="h-4 bg-gray-200 rounded mb-2 animate-pulse"></div>
                  <div className="h-4 bg-gray-200 rounded w-3/4 mb-2 animate-pulse"></div>
                  <div className="h-6 bg-gray-200 rounded w-1/2 animate-pulse"></div>
                </div>
              ))
            ) : (
              // Real products from API
              featuredProducts.map((product) => (
                <div key={product.id} className="product-card group">
                  <div className="relative">
                    <Link to={`/products/${product.id}`}>
                      <div className="aspect-square bg-gray-200 mb-4 flex items-center justify-center overflow-hidden rounded-lg">
                        <img 
                          src={product.image} 
                          alt={product.name}
                          className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-300"
                          onError={(e) => {
                            e.target.style.display = 'none';
                            e.target.nextSibling.style.display = 'flex';
                          }}
                        />
                        <div className="hidden w-full h-full items-center justify-center text-gray-500 text-center p-4">
                          <span className="text-sm">{product.name}</span>
                        </div>
                      </div>
                    </Link>
                    
                    {/* Wishlist Button */}
                    <div className="absolute top-3 right-3">
                      <WishlistButton product={product} />
                    </div>
                    
                    {/* Compare Button */}
                    <div className="absolute top-3 left-3">
                      <CompareButton product={product} showText={false} size="small" />
                    </div>
                  </div>
                  
                  <Link to={`/products/${product.id}`}>
                    <div className="p-2">
                      <h3 className="text-lg font-medium text-black mb-2 group-hover:text-gray-700 transition-colors line-clamp-2">
                        {product.name}
                      </h3>
                      <p className="text-gray-600 mb-2 capitalize">{product.category}</p>
                      
                      <div className="mb-2">
                        <StarRating
                          rating={getProductRating(product.id).average}
                          showCount={true}
                          count={getProductRating(product.id).count}
                          size="small"
                        />
                      </div>
                      
                      <div className="flex items-center justify-between">
                        <p className="text-xl font-semibold text-black">${product.price}</p>
                      </div>
                    </div>
                  </Link>
                </div>
              ))
            )}
          </div>
        </div>
      </section>

      {/* Categories Section */}
      <section className="py-20 bg-gray-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-16">
            <h2 className="text-4xl font-light text-black mb-4">Shop by Category</h2>
            <p className="text-gray-600 text-lg">Find exactly what you're looking for</p>
          </div>
          
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            {[
              { name: 'Clothes', image: '/images/product_15.png' },
              { name: 'Shoes', image: '/images/shoes/nike-jordan.webp' }
            ].map((category) => (
              <Link 
                key={category.name}
                to={`/${category.name.toLowerCase()}`}
                className="group relative h-80 bg-gray-300 overflow-hidden flex items-center justify-center hover:scale-105 transition-transform duration-300"
              >
                <img 
                  src={category.image} 
                  alt={category.name}
                  className="absolute inset-0 w-full h-full object-cover group-hover:scale-110 transition-transform duration-300"
                />
                <div className="absolute inset-0 bg-black bg-opacity-40 group-hover:bg-opacity-50 transition-all duration-300"></div>
                <div className="relative z-10 text-center text-white">
                  <h3 className="text-3xl font-light mb-2">{category.name}</h3>
                  <p className="text-sm tracking-wider">SHOP NOW</p>
                </div>
              </Link>
            ))}
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
