import React, { useState, useEffect } from 'react';
import localProductService from '../services/localProductService';

const DebugProducts = () => {
  const [allProducts, setAllProducts] = useState([]);
  const [womenProducts, setWomenProducts] = useState([]);
  const [menProducts, setMenProducts] = useState([]);
  const [kidProducts, setKidProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const testProducts = async () => {
      try {
        console.log('Testing localProductService...');
        
        // Test getProducts method
        const all = await localProductService.getProducts();
        console.log('All products:', all.length);
        setAllProducts(all);
        
        // Test filtering
        const women = all.filter(p => p.category === 'women');
        const men = all.filter(p => p.category === 'men');
        const kids = all.filter(p => p.category === 'kid');
        
        console.log('Women products:', women.length);
        console.log('Men products:', men.length);
        console.log('Kids products:', kids.length);
        
        setWomenProducts(women);
        setMenProducts(men);
        setKidProducts(kids);
        
      } catch (err) {
        console.error('Error testing products:', err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    testProducts();
  }, []);

  if (loading) return <div className="p-4">Loading...</div>;
  if (error) return <div className="p-4 text-red-500">Error: {error}</div>;

  return (
    <div className="p-4 max-w-4xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Product Debug</h1>
      
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-8">
        <div className="bg-blue-100 p-4 rounded">
          <h3 className="font-bold">Total Products</h3>
          <p className="text-2xl">{allProducts.length}</p>
        </div>
        <div className="bg-pink-100 p-4 rounded">
          <h3 className="font-bold">Women</h3>
          <p className="text-2xl">{womenProducts.length}</p>
        </div>
        <div className="bg-blue-200 p-4 rounded">
          <h3 className="font-bold">Men</h3>
          <p className="text-2xl">{menProducts.length}</p>
        </div>
        <div className="bg-yellow-100 p-4 rounded">
          <h3 className="font-bold">Kids</h3>
          <p className="text-2xl">{kidProducts.length}</p>
        </div>
      </div>
      
      <div className="space-y-4">
        <h2 className="text-xl font-bold">Sample Women Products:</h2>
        {womenProducts.slice(0, 3).map(product => (
          <div key={product.id} className="border p-3 rounded flex items-center space-x-4">
            <img src={product.image} alt={product.name} className="w-16 h-16 object-cover rounded" />
            <div>
              <h3 className="font-semibold">{product.name}</h3>
              <p className="text-gray-600">Category: {product.category}</p>
              <p className="text-green-600">${product.price}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DebugProducts;
