import React, { useState, useEffect } from 'react';
import { Link, useSearchParams } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { useSearch } from '../context/SearchContext';
import { useReviews } from '../context/ReviewsContext';
import WishlistButton from '../components/WishlistButton';
import StarRating from '../components/StarRating';
import SearchFilters from '../components/SearchFilters';
import SortDropdown from '../components/SortDropdown';

const Products = () => {
  const [searchParams] = useSearchParams();
  const { addItem } = useCart();
  const { getProductRating } = useReviews();
  const {
    searchResults,
    isSearching,
    searchQuery,
    setSearchQuery,
    filters,
    updateFilter,
    clearFilters,
    getActiveFiltersCount,
    showFilters,
    setShowFilters
  } = useSearch();

  // Initialize search based on URL parameters
  useEffect(() => {
    const searchParam = searchParams.get('search');
    const categoryParam = searchParams.get('category');
    const brandParam = searchParams.get('brand');
    
    if (searchParam) {
      setSearchQuery(searchParam);
    }
    
    if (categoryParam) {
      updateFilter('category', categoryParam);
    }
    
    if (brandParam) {
      updateFilter('brand', brandParam);
    }
  }, [searchParams, setSearchQuery, updateFilter]);

  const handleAddToCart = (product) => {
    addItem({
      id: product.id,
      name: product.name,
      price: product.price,
      image: product.image,
      size: product.sizes?.[0] || 'One Size',
      color: product.colors?.[0] || 'Default',
      quantity: 1
    });
  };

  const getProductRatingData = (productId) => {
    const rating = getProductRating(productId);
    return {
      average: rating.averageRating,
      count: rating.totalReviews
    };
  };

  const displayProducts = searchResults;
  const activeFiltersCount = getActiveFiltersCount();

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="mb-8">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-light text-black mb-2">
                {searchQuery ? `Search Results for "${searchQuery}"` : 'All Products'}
              </h1>
              <p className="text-gray-600">
                {isSearching ? 'Searching...' : `${displayProducts.length} products found`}
                {activeFiltersCount > 0 && ` (${activeFiltersCount} filters applied)`}
              </p>
            </div>
          </div>

          {/* Active Filters Display */}
          {activeFiltersCount > 0 && (
            <div className="mt-4 flex flex-wrap items-center gap-2">
              <span className="text-sm text-gray-600">Active filters:</span>
              
              {filters.category && (
                <span className="inline-flex items-center px-3 py-1 bg-blue-100 text-blue-800 text-sm rounded-full">
                  Category: {filters.category}
                  <button
                    onClick={() => updateFilter('category', '')}
                    className="ml-2 text-blue-600 hover:text-blue-800"
                  >
                    ×
                  </button>
                </span>
              )}
              
              {filters.brand && (
                <span className="inline-flex items-center px-3 py-1 bg-blue-100 text-blue-800 text-sm rounded-full">
                  Brand: {filters.brand}
                  <button
                    onClick={() => updateFilter('brand', '')}
                    className="ml-2 text-blue-600 hover:text-blue-800"
                  >
                    ×
                  </button>
                </span>
              )}
              
              {filters.size && (
                <span className="inline-flex items-center px-3 py-1 bg-blue-100 text-blue-800 text-sm rounded-full">
                  Size: {filters.size}
                  <button
                    onClick={() => updateFilter('size', '')}
                    className="ml-2 text-blue-600 hover:text-blue-800"
                  >
                    ×
                  </button>
                </span>
              )}
              
              {filters.color && (
                <span className="inline-flex items-center px-3 py-1 bg-blue-100 text-blue-800 text-sm rounded-full">
                  Color: {filters.color}
                  <button
                    onClick={() => updateFilter('color', '')}
                    className="ml-2 text-blue-600 hover:text-blue-800"
                  >
                    ×
                  </button>
                </span>
              )}
              
              {filters.rating > 0 && (
                <span className="inline-flex items-center px-3 py-1 bg-blue-100 text-blue-800 text-sm rounded-full">
                  {filters.rating}+ stars
                  <button
                    onClick={() => updateFilter('rating', 0)}
                    className="ml-2 text-blue-600 hover:text-blue-800"
                  >
                    ×
                  </button>
                </span>
              )}
              
              {(filters.priceRange[0] > 0 || filters.priceRange[1] < 1000) && (
                <span className="inline-flex items-center px-3 py-1 bg-blue-100 text-blue-800 text-sm rounded-full">
                  ${filters.priceRange[0]} - ${filters.priceRange[1]}
                  <button
                    onClick={() => updateFilter('priceRange', [0, 1000])}
                    className="ml-2 text-blue-600 hover:text-blue-800"
                  >
                    ×
                  </button>
                </span>
              )}
              
              {(filters.inStock || filters.onSale) && (
                <span className="inline-flex items-center px-3 py-1 bg-blue-100 text-blue-800 text-sm rounded-full">
                  {filters.inStock && 'In Stock'}{filters.inStock && filters.onSale && ' & '}{filters.onSale && 'On Sale'}
                  <button
                    onClick={() => {
                      updateFilter('inStock', false);
                      updateFilter('onSale', false);
                    }}
                    className="ml-2 text-blue-600 hover:text-blue-800"
                  >
                    ×
                  </button>
                </span>
              )}
              
              <button
                onClick={clearFilters}
                className="text-sm text-red-600 hover:text-red-800"
              >
                Clear all filters
              </button>
            </div>
          )}
        </div>

        <div className="flex gap-8">
          {/* Filters Sidebar */}
          <div className="w-80 flex-shrink-0">
            <SearchFilters />
          </div>

          {/* Products Grid */}
          <div className="flex-1">
            {/* Sort and View Options */}
            <div className="flex items-center justify-between mb-6">
              <div className="flex items-center space-x-4">
                {!showFilters && (
                  <button
                    onClick={() => setShowFilters(true)}
                    className="md:hidden flex items-center space-x-2 px-4 py-2 border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors"
                  >
                    <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 4a1 1 0 011-1h16a1 1 0 011 1v2.586a1 1 0 01-.293.707l-6.414 6.414a1 1 0 00-.293.707V17l-4 4v-6.586a1 1 0 00-.293-.707L3.293 7.207A1 1 0 013 6.5V4z" />
                    </svg>
                    <span>Filters</span>
                    {activeFiltersCount > 0 && (
                      <span className="px-2 py-1 bg-blue-100 text-blue-800 text-xs rounded-full">
                        {activeFiltersCount}
                      </span>
                    )}
                  </button>
                )}
              </div>
              
              <SortDropdown />
            </div>

            {/* Products Grid */}
            {isSearching ? (
              <div className="flex items-center justify-center py-20">
                <div className="text-center">
                  <svg className="w-8 h-8 mx-auto mb-4 text-gray-400 animate-spin" fill="none" viewBox="0 0 24 24">
                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" />
                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z" />
                  </svg>
                  <p className="text-gray-600">Searching products...</p>
                </div>
              </div>
            ) : displayProducts.length === 0 ? (
              <div className="text-center py-20">
                <div className="w-24 h-24 mx-auto mb-6 bg-gray-100 rounded-full flex items-center justify-center">
                  <svg className="w-12 h-12 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1} d="M20 13V6a2 2 0 00-2-2H6a2 2 0 00-2 2v7m16 0v5a2 2 0 01-2 2H6a2 2 0 01-2-2v-5m16 0h-2.586a1 1 0 00-.707.293l-2.414 2.414a1 1 0 01-.707.293h-3.172a1 1 0 01-.707-.293l-2.414-2.414A1 1 0 006.586 13H4" />
                  </svg>
                </div>
                <h3 className="text-xl font-medium text-black mb-2">No products found</h3>
                <p className="text-gray-600 mb-6">
                  {searchQuery || activeFiltersCount > 0
                    ? 'Try adjusting your search or filters to find what you\'re looking for.'
                    : 'No products are available at the moment.'
                  }
                </p>
                {(searchQuery || activeFiltersCount > 0) && (
                  <button
                    onClick={() => {
                      setSearchQuery('');
                      clearFilters();
                    }}
                    className="btn-primary"
                  >
                    Clear Search & Filters
                  </button>
                )}
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
                {displayProducts.map((product) => {
                  const ratingData = getProductRatingData(product.id);
                  
                  return (
                    <div key={product.id} className="group relative bg-white rounded-lg shadow-sm hover:shadow-lg transition-shadow">
                      <div className="relative overflow-hidden rounded-t-lg">
                        <Link to={`/products/${product.id}`}>
                          <img
                            src={product.image}
                            alt={product.name}
                            className="w-full h-80 object-cover group-hover:scale-105 transition-transform duration-300"
                            onError={(e) => {
                              e.target.src = '/images/placeholder-product.png';
                            }}
                          />
                        </Link>
                        
                        {/* Sale Badge */}
                        {product.originalPrice && product.originalPrice > product.price && (
                          <div className="absolute top-4 left-4 bg-red-500 text-white px-2 py-1 rounded-md text-sm font-medium">
                            SALE
                          </div>
                        )}
                        
                        {/* Wishlist Button */}
                        <div className="absolute top-4 right-4">
                          <WishlistButton productId={product.id} />
                        </div>
                      </div>

                      <div className="p-6">
                        <div className="flex items-start justify-between mb-2">
                          <Link to={`/products/${product.id}`}>
                            <h3 className="font-medium text-black group-hover:text-gray-700 transition-colors line-clamp-2">
                              {product.name}
                            </h3>
                          </Link>
                        </div>

                        {/* Rating */}
                        <div className="flex items-center mb-2">
                          <StarRating rating={ratingData.average} size="sm" />
                          <span className="ml-2 text-sm text-gray-600">
                            ({ratingData.count})
                          </span>
                        </div>

                        {/* Category and Brand */}
                        <div className="text-sm text-gray-500 mb-2 capitalize">
                          {product.category}
                          {product.brand && ` • ${product.brand}`}
                        </div>

                        {/* Price */}
                        <div className="flex items-center mb-4">
                          <span className="text-xl font-bold text-black">${product.price}</span>
                          {product.originalPrice && product.originalPrice > product.price && (
                            <span className="ml-2 text-sm text-gray-500 line-through">
                              ${product.originalPrice}
                            </span>
                          )}
                        </div>

                        {/* Add to Cart Button */}
                        <button
                          onClick={() => handleAddToCart(product)}
                          className="w-full btn-primary"
                        >
                          Add to Cart
                        </button>
                      </div>
                    </div>
                  );
                })}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Products;
