import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { useAuth } from '../context/AuthContext';
import { useWishlist } from '../context/WishlistContext';
import SearchBar from './SearchBar';

const Navbar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [activeDropdown, setActiveDropdown] = useState(null);
  const [isAccountDropdownOpen, setIsAccountDropdownOpen] = useState(false);
  const { getCartItemsCount } = useCart();
  const { isAuthenticated, user, logout } = useAuth();
  const { getWishlistCount } = useWishlist();
  const navigate = useNavigate();
  const cartCount = getCartItemsCount();
  const wishlistCount = getWishlistCount();
  const dropdownRefs = useRef({});

  // Handle clicking outside dropdowns
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (activeDropdown && dropdownRefs.current[activeDropdown]) {
        if (!dropdownRefs.current[activeDropdown].contains(event.target)) {
          setActiveDropdown(null);
        }
      }
      if (isAccountDropdownOpen && dropdownRefs.current.account) {
        if (!dropdownRefs.current.account.contains(event.target)) {
          setIsAccountDropdownOpen(false);
        }
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, [activeDropdown, isAccountDropdownOpen]);

  const handleLogout = () => {
    logout();
    setIsAccountDropdownOpen(false);
    navigate('/');
  };

  // Navigation categories with dropdown items
  const navigationCategories = {
    clothes: {
      title: 'CLOTHES',
      items: [
        { name: 'All Clothes', path: '/clothes' },
        { name: 'Dresses', path: '/clothes?category=dresses' },
        { name: 'Tops', path: '/clothes?category=tops' },
        { name: 'Jackets', path: '/clothes?category=jackets' },
        { name: 'Pants', path: '/clothes?category=pants' }
      ]
    },
    shoes: {
      title: 'SHOES',
      items: [
        { name: 'All Shoes', path: '/shoes' },
        { name: 'Sneakers', path: '/shoes?category=sneakers' },
        { name: 'Boots', path: '/shoes?category=boots' },
        { name: 'Sandals', path: '/shoes?category=sandals' },
        { name: 'Heels', path: '/shoes?category=heels' }
      ]
    },
    women: {
      title: 'WOMEN',
      items: [
        { name: 'All Women', path: '/women' },
        { name: 'New Arrivals', path: '/women?filter=new' },
        { name: 'Trending', path: '/women?filter=trending' },
        { name: 'Sale Items', path: '/women?filter=sale' }
      ]
    },
    men: {
      title: 'MEN',
      items: [
        { name: 'All Men', path: '/men' },
        { name: 'New Arrivals', path: '/men?filter=new' },
        { name: 'Trending', path: '/men?filter=trending' },
        { name: 'Sale Items', path: '/men?filter=sale' }
      ]
    },
    kids: {
      title: 'KIDS',
      items: [
        { name: 'All Kids', path: '/kids' },
        { name: 'Boys', path: '/kids?category=boys' },
        { name: 'Girls', path: '/kids?category=girls' },
        { name: 'Baby', path: '/kids?category=baby' }
      ]
    }
  };

  return (
    <>
      {/* Main Navigation */}
      <nav className="bg-white shadow-sm border-b relative z-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            {/* Left Side - Navigation Links */}
            <div className="hidden lg:flex items-center space-x-8">
              <Link to="/products?filter=new" className="nav-link hover:text-gray-900 transition-colors">NEW IN</Link>
              <Link to="/products?filter=sale" className="nav-link text-red-600 hover:text-red-700 transition-colors">SALE</Link>
              
              {/* Dropdown Categories */}
              {Object.entries(navigationCategories).map(([key, category]) => (
                <div 
                  key={key} 
                  className="relative"
                  ref={el => dropdownRefs.current[key] = el}
                  onMouseEnter={() => setActiveDropdown(key)}
                  onMouseLeave={() => setActiveDropdown(null)}
                >
                  <button className="nav-link hover:text-gray-900 transition-colors flex items-center space-x-1">
                    <span>{category.title}</span>
                    <svg 
                      className={`w-4 h-4 transition-transform duration-200 ${
                        activeDropdown === key ? 'rotate-180' : ''
                      }`} 
                      fill="none" 
                      stroke="currentColor" 
                      viewBox="0 0 24 24"
                    >
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                    </svg>
                  </button>
                  
                  {/* Dropdown Menu */}
                  <div className={`absolute left-0 mt-2 w-48 bg-white rounded-lg shadow-lg border border-gray-200 py-2 transition-all duration-200 transform ${
                    activeDropdown === key 
                      ? 'opacity-100 translate-y-0 visible' 
                      : 'opacity-0 translate-y-2 invisible'
                  }`}>
                    {category.items.map((item, index) => (
                      <Link
                        key={index}
                        to={item.path}
                        className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 hover:text-gray-900 transition-colors"
                        onClick={() => setActiveDropdown(null)}
                      >
                        {item.name}
                      </Link>
                    ))}
                  </div>
                </div>
              ))}
            </div>

            {/* Center - Logo */}
            <Link to="/" className="flex items-center space-x-2 flex-shrink-0">
              <div className="w-10 h-10 bg-gradient-to-br from-gray-900 to-black rounded-full flex items-center justify-center shadow-lg">
                <span className="text-white font-bold text-lg">TG</span>
              </div>
              <span className="text-2xl font-bold tracking-wider bg-gradient-to-r from-gray-900 to-black bg-clip-text text-transparent">
                THEGEM
              </span>
            </Link>

            {/* Right Side - Search, Account, Wishlist, Cart */}
            <div className="flex items-center space-x-4">
              {/* Search Bar */}
              <div className="hidden md:block">
                <SearchBar className="w-56" placeholder="Search products..." />
              </div>
              
              {/* Account Dropdown */}
              {isAuthenticated ? (
                <div 
                  className="relative"
                  ref={el => dropdownRefs.current.account = el}
                >
                  <button
                    onClick={() => setIsAccountDropdownOpen(!isAccountDropdownOpen)}
                    className="flex items-center space-x-2 px-3 py-2 rounded-lg hover:bg-gray-100 transition-colors"
                  >
                    <div className="w-8 h-8 bg-gradient-to-br from-gray-600 to-gray-800 rounded-full flex items-center justify-center">
                      <span className="text-white text-sm font-semibold">
                        {user?.firstName?.charAt(0) || user?.email?.charAt(0) || 'U'}
                      </span>
                    </div>
                    <span className="hidden md:block text-sm font-medium text-gray-700">
                      {user?.firstName || 'Account'}
                    </span>
                    <svg 
                      className={`w-4 h-4 transition-transform duration-200 ${
                        isAccountDropdownOpen ? 'rotate-180' : ''
                      }`} 
                      fill="none" 
                      stroke="currentColor" 
                      viewBox="0 0 24 24"
                    >
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                    </svg>
                  </button>
                  
                  {/* Account Dropdown Menu */}
                  <div className={`absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg border border-gray-200 py-2 transition-all duration-200 transform ${
                    isAccountDropdownOpen 
                      ? 'opacity-100 translate-y-0 visible' 
                      : 'opacity-0 translate-y-2 invisible'
                  }`}>
                    <Link
                      to="/profile"
                      className="flex items-center px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 transition-colors"
                      onClick={() => setIsAccountDropdownOpen(false)}
                    >
                      <svg className="w-4 h-4 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                      </svg>
                      My Profile
                    </Link>
                    <Link
                      to="/profile"
                      className="flex items-center px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 transition-colors"
                      onClick={() => setIsAccountDropdownOpen(false)}
                    >
                      <svg className="w-4 h-4 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                      </svg>
                      My Orders
                    </Link>
                    <Link
                      to="/compare"
                      className="flex items-center px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 transition-colors"
                      onClick={() => setIsAccountDropdownOpen(false)}
                    >
                      <svg className="w-4 h-4 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
                      </svg>
                      Compare Products
                    </Link>
                    <hr className="my-2 border-gray-200" />
                    <button
                      onClick={handleLogout}
                      className="flex items-center w-full px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 transition-colors"
                    >
                      <svg className="w-4 h-4 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h4a3 3 0 013 3v1" />
                      </svg>
                      Sign Out
                    </button>
                  </div>
                </div>
              ) : (
                <Link 
                  to="/auth" 
                  className="px-4 py-2 text-sm font-medium text-gray-700 hover:text-gray-900 hover:bg-gray-100 rounded-lg transition-colors"
                >
                  Sign In
                </Link>
              )}

              {/* Wishlist Icon */}
              {isAuthenticated && (
                <Link 
                  to="/wishlist" 
                  className="relative p-2 hover:bg-gray-100 rounded-lg transition-colors group"
                  title="Wishlist"
                >
                  <svg className="w-6 h-6 text-gray-600 group-hover:text-red-500 transition-colors" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z" />
                  </svg>
                  {wishlistCount > 0 && (
                    <span className="absolute -top-1 -right-1 bg-red-500 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center animate-pulse">
                      {wishlistCount}
                    </span>
                  )}
                </Link>
              )}
              
              {/* Cart Icon */}
              <Link 
                to="/cart" 
                className="relative p-2 hover:bg-gray-100 rounded-lg transition-colors group"
                title="Shopping Cart"
              >
                <svg className="w-6 h-6 text-gray-600 group-hover:text-blue-500 transition-colors" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 3h2l.4 2M7 13h10l4-8H5.4m0 0L7 13m0 0l-2.5-5M7 13l-2.5 5M17 21a2 2 0 100-4 2 2 0 000 4zM9 21a2 2 0 100-4 2 2 0 000 4z" />
                </svg>
                {cartCount > 0 && (
                  <span className="absolute -top-1 -right-1 bg-blue-500 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center animate-pulse">
                    {cartCount}
                  </span>
                )}
              </Link>
              
              {/* Mobile menu button */}
              <button
                className="lg:hidden p-2 hover:bg-gray-100 rounded-lg transition-colors"
                onClick={() => setIsMenuOpen(!isMenuOpen)}
              >
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
                </svg>
              </button>
            </div>
          </div>
        </div>

        {/* Mobile Navigation Menu */}
        {isMenuOpen && (
          <div className="lg:hidden bg-white border-t shadow-lg">
            <div className="px-4 py-4 space-y-4">
              {/* Mobile Search */}
              <div className="pb-2">
                <SearchBar className="w-full" placeholder="Search products..." />
              </div>
              
              <hr className="border-gray-200" />
              
              {/* Mobile Account Section */}
              {isAuthenticated ? (
                <div className="flex items-center space-x-3 py-2">
                  <div className="w-8 h-8 bg-gradient-to-br from-gray-600 to-gray-800 rounded-full flex items-center justify-center">
                    <span className="text-white text-sm font-semibold">
                      {user?.firstName?.charAt(0) || user?.email?.charAt(0) || 'U'}
                    </span>
                  </div>
                  <div>
                    <p className="text-sm font-medium text-gray-900">
                      {user?.firstName || 'Account'}
                    </p>
                    <p className="text-xs text-gray-500">{user?.email}</p>
                  </div>
                </div>
              ) : (
                <Link 
                  to="/auth" 
                  className="block py-2 text-sm font-medium text-blue-600 hover:text-blue-700"
                  onClick={() => setIsMenuOpen(false)}
                >
                  Sign In / Register
                </Link>
              )}
              
              <hr className="border-gray-200" />
              
              {/* Mobile Navigation Links */}
              <div className="space-y-3">
                <Link 
                  to="/products?filter=new" 
                  className="block py-2 text-sm font-medium text-gray-900 hover:text-blue-600 transition-colors"
                  onClick={() => setIsMenuOpen(false)}
                >
                  NEW IN
                </Link>
                <Link 
                  to="/products?filter=sale" 
                  className="block py-2 text-sm font-medium text-red-600 hover:text-red-700 transition-colors"
                  onClick={() => setIsMenuOpen(false)}
                >
                  SALE
                </Link>
                
                {/* Mobile Category Links */}
                {Object.entries(navigationCategories).map(([key, category]) => (
                  <div key={key} className="space-y-1">
                    <p className="text-sm font-semibold text-gray-900 py-1">{category.title}</p>
                    <div className="pl-4 space-y-1">
                      {category.items.map((item, index) => (
                        <Link
                          key={index}
                          to={item.path}
                          className="block py-1 text-sm text-gray-600 hover:text-blue-600 transition-colors"
                          onClick={() => setIsMenuOpen(false)}
                        >
                          {item.name}
                        </Link>
                      ))}
                    </div>
                  </div>
                ))}
                
                <Link 
                  to="/products" 
                  className="block py-2 text-sm font-medium text-gray-900 hover:text-blue-600 transition-colors"
                  onClick={() => setIsMenuOpen(false)}
                >
                  MAIN SHOP
                </Link>
              </div>
              
              {/* Mobile Account Actions */}
              {isAuthenticated && (
                <>
                  <hr className="border-gray-200" />
                  <div className="space-y-2">
                    <Link
                      to="/profile"
                      className="flex items-center py-2 text-sm text-gray-700 hover:text-blue-600 transition-colors"
                      onClick={() => setIsMenuOpen(false)}
                    >
                      <svg className="w-4 h-4 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                      </svg>
                      My Profile
                    </Link>
                    <Link
                      to="/wishlist"
                      className="flex items-center py-2 text-sm text-gray-700 hover:text-blue-600 transition-colors"
                      onClick={() => setIsMenuOpen(false)}
                    >
                      <svg className="w-4 h-4 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z" />
                      </svg>
                      Wishlist ({wishlistCount})
                    </Link>
                    <Link
                      to="/cart"
                      className="flex items-center py-2 text-sm text-gray-700 hover:text-blue-600 transition-colors"
                      onClick={() => setIsMenuOpen(false)}
                    >
                      <svg className="w-4 h-4 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 3h2l.4 2M7 13h10l4-8H5.4m0 0L7 13m0 0l-2.5-5M7 13l-2.5 5M17 21a2 2 0 100-4 2 2 0 000 4zM9 21a2 2 0 100-4 2 2 0 000 4z" />
                      </svg>
                      Cart ({cartCount})
                    </Link>
                    <Link
                      to="/compare"
                      className="flex items-center py-2 text-sm text-gray-700 hover:text-blue-600 transition-colors"
                      onClick={() => setIsMenuOpen(false)}
                    >
                      <svg className="w-4 h-4 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
                      </svg>
                      Compare Products
                    </Link>
                    <button
                      onClick={() => {
                        handleLogout();
                        setIsMenuOpen(false);
                      }}
                      className="flex items-center w-full py-2 text-sm text-gray-700 hover:text-red-600 transition-colors"
                    >
                      <svg className="w-4 h-4 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h4a3 3 0 013 3v1" />
                      </svg>
                      Sign Out
                    </button>
                  </div>
                </>
              )}
            </div>
          </div>
        )}
      </nav>
    </>
  );
};

export default Navbar;
