import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { useAuth } from '../context/AuthContext';
import { useWishlist } from '../context/WishlistContext';
import SearchBar from './SearchBar';

const Navbar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [activeDropdown, setActiveDropdown] = useState(null);
  const [isAccountDropdownOpen, setIsAccountDropdownOpen] = useState(false);
  const { getCartItemsCount } = useCart();
  const { isAuthenticated, user, logout } = useAuth();
  const { getWishlistCount } = useWishlist();
  const navigate = useNavigate();
  const cartCount = getCartItemsCount();
  const wishlistCount = getWishlistCount();
  const dropdownRefs = useRef({});

  // Handle clicking outside dropdowns
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (activeDropdown && dropdownRefs.current[activeDropdown]) {
        if (!dropdownRefs.current[activeDropdown].contains(event.target)) {
          setActiveDropdown(null);
        }
      }
      if (isAccountDropdownOpen && dropdownRefs.current.account) {
        if (!dropdownRefs.current.account.contains(event.target)) {
          setIsAccountDropdownOpen(false);
        }
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, [activeDropdown, isAccountDropdownOpen]);

  const handleLogout = () => {
    logout();
    setIsAccountDropdownOpen(false);
    navigate('/');
  };

  // Navigation categories with dropdown items
  const navigationCategories = {
    clothes: {
      title: 'CLOTHES',
      items: [
        { name: 'All Clothes', path: '/clothes' },
        { name: 'Dresses', path: '/clothes?category=dresses' },
        { name: 'Tops', path: '/clothes?category=tops' },
        { name: 'Jackets', path: '/clothes?category=jackets' },
        { name: 'Pants', path: '/clothes?category=pants' }
      ]
    },
    shoes: {
      title: 'SHOES',
      items: [
        { name: 'All Shoes', path: '/shoes' },
        { name: 'Sneakers', path: '/shoes?category=sneakers' },
        { name: 'Boots', path: '/shoes?category=boots' },
        { name: 'Sandals', path: '/shoes?category=sandals' },
        { name: 'Heels', path: '/shoes?category=heels' }
      ]
    },
    women: {
      title: 'WOMEN',
      items: [
        { name: 'All Women', path: '/women' },
        { name: 'New Arrivals', path: '/women?filter=new' },
        { name: 'Trending', path: '/women?filter=trending' },
        { name: 'Sale Items', path: '/women?filter=sale' }
      ]
    },
    men: {
      title: 'MEN',
      items: [
        { name: 'All Men', path: '/men' },
        { name: 'New Arrivals', path: '/men?filter=new' },
        { name: 'Trending', path: '/men?filter=trending' },
        { name: 'Sale Items', path: '/men?filter=sale' }
      ]
    },
    kids: {
      title: 'KIDS',
      items: [
        { name: 'All Kids', path: '/kids' },
        { name: 'Boys', path: '/kids?category=boys' },
        { name: 'Girls', path: '/kids?category=girls' },
        { name: 'Baby', path: '/kids?category=baby' }
      ]
    }
  };

  return (
    <>
      {/* Main Navigation */}
      <nav className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            {/* Logo */}
            <Link to="/" className="flex items-center space-x-2">
              <div className="w-8 h-8 bg-black rounded-full flex items-center justify-center">
                <span className="text-white font-bold text-sm">TG</span>
              </div>
              <span className="text-xl font-bold tracking-wider">THEGEM</span>
            </Link>

            {/* Desktop Navigation Links */}
            <div className="hidden md:flex items-center space-x-8">
              <Link to="/products?filter=new" className="nav-link">NEW IN</Link>
              <Link to="/products?filter=sale" className="nav-link text-red-600">SALE</Link>
              <Link to="/clothes" className="nav-link">CLOTHES</Link>
              <Link to="/shoes" className="nav-link">SHOES</Link>
              <Link to="/women" className="nav-link">WOMEN</Link>
              <Link to="/men" className="nav-link">MEN</Link>
              <Link to="/kids" className="nav-link">KIDS</Link>
              <Link to="/products" className="nav-link">MAIN SHOP</Link>
            </div>

            {/* Search and Cart Icons */}
            <div className="flex items-center space-x-4">
              {/* Search Bar */}
              <div className="hidden md:block max-w-xs">
                <SearchBar className="w-48" placeholder="Search..." />
              </div>
              
              {/* Login/Profile Button */}
              {isAuthenticated ? (
                <div className="hidden md:flex items-center space-x-2">
                  <Link 
                    to="/profile" 
                    className="flex items-center space-x-2 px-4 py-2 text-sm font-medium text-gray-700 hover:text-black transition-colors"
                  >
                    <div className="w-6 h-6 bg-gray-200 rounded-full flex items-center justify-center">
                      <span className="text-xs font-semibold text-gray-600">
                        {user?.firstName?.charAt(0) || user?.email?.charAt(0) || 'U'}
                      </span>
                    </div>
                    <span>Account</span>
                  </Link>
                </div>
              ) : (
                <Link to="/auth" className="hidden md:block px-4 py-2 text-sm font-medium text-gray-700 hover:text-black transition-colors">
                  Login
                </Link>
              )}

              {/* Wishlist Icon */}
              {isAuthenticated && (
                <Link to="/wishlist" className="p-2 hover:bg-gray-100 rounded-lg transition-colors relative">
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z" />
                  </svg>
                  {wishlistCount > 0 && (
                    <span className="absolute -top-1 -right-1 bg-black text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
                      {wishlistCount}
                    </span>
                  )}
                </Link>
              )}
              
              <Link to="/cart" className="p-2 hover:bg-gray-100 rounded-lg transition-colors relative">
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 3h2l.4 2M7 13h10l4-8H5.4m0 0L7 13m0 0l-2.5-5M7 13l-2.5 5M17 21a2 2 0 100-4 2 2 0 000 4zM9 21a2 2 0 100-4 2 2 0 000 4z" />
                </svg>
                {cartCount > 0 && (
                  <span className="absolute -top-1 -right-1 bg-black text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
                    {cartCount}
                  </span>
                )}
              </Link>
              
              {/* Mobile menu button */}
              <button
                className="md:hidden p-2 hover:bg-gray-100 rounded-lg transition-colors"
                onClick={() => setIsMenuOpen(!isMenuOpen)}
              >
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
                </svg>
              </button>
            </div>
          </div>
        </div>

        {/* Mobile Navigation Menu */}
        {isMenuOpen && (
          <div className="md:hidden bg-white border-t">
            <div className="px-4 py-2 space-y-2">
              {/* Mobile Search */}
              <div className="py-2">
                <SearchBar className="w-full" placeholder="Search products..." />
              </div>
              <hr className="border-gray-200" />
              <Link to="/products?filter=new" className="block py-2 nav-link">NEW IN</Link>
              <Link to="/products?filter=sale" className="block py-2 nav-link text-red-600">SALE</Link>
              <Link to="/clothes" className="block py-2 nav-link">CLOTHES</Link>
              <Link to="/shoes" className="block py-2 nav-link">SHOES</Link>
              <Link to="/women" className="block py-2 nav-link">WOMEN</Link>
              <Link to="/men" className="block py-2 nav-link">MEN</Link>
              <Link to="/kids" className="block py-2 nav-link">KIDS</Link>
              <Link to="/products" className="block py-2 nav-link">MAIN SHOP</Link>
            </div>
          </div>
        )}
      </nav>
    </>
  );
};

export default Navbar;
