import React, { useState, useEffect } from 'react';
import { Link, useSearchParams } from 'react-router-dom';
import localProductService from '../services/localProductService';

const Products = () => {
  const [searchParams] = useSearchParams();
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    category: searchParams.get('category') || '',
    filter: searchParams.get('filter') || '',
    search: '',
    priceRange: 'all'
  });

  useEffect(() => {
    // Fetch products from local service
    const loadProducts = async () => {
      setLoading(true);
      try {
        const localProducts = await localProductService.getAllProducts();
        setProducts(localProducts);
      } catch (error) {
        console.error('Error loading products:', error);
      } finally {
        setLoading(false);
      }
    };

    loadProducts();
  }, []);

  useEffect(() => {
    let filtered = products;

    // Filter by category
    if (filters.category) {
      filtered = filtered.filter(product => product.category === filters.category);
    }

    // Filter by tags (new, sale, etc.)
    if (filters.filter) {
      filtered = filtered.filter(product => product.tags.includes(filters.filter));
    }

    // Filter by search term
    if (filters.search) {
      filtered = filtered.filter(product => 
        product.name.toLowerCase().includes(filters.search.toLowerCase())
      );
    }

    // Filter by price range
    if (filters.priceRange !== 'all') {
      switch (filters.priceRange) {
        case 'under50':
          filtered = filtered.filter(product => product.price < 50);
          break;
        case '50to100':
          filtered = filtered.filter(product => product.price >= 50 && product.price <= 100);
          break;
        case '100to200':
          filtered = filtered.filter(product => product.price >= 100 && product.price <= 200);
          break;
        case 'over200':
          filtered = filtered.filter(product => product.price > 200);
          break;
        default:
          break;
      }
    }

    setFilteredProducts(filtered);
  }, [products, filters]);

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({ ...prev, [key]: value }));
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-black mx-auto mb-4"></div>
          <p className="text-gray-600">Loading products...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Page Header */}
        <div className="mb-8">
          <h1 className="text-4xl font-light text-black mb-4">
            {filters.category ? `${filters.category.charAt(0).toUpperCase() + filters.category.slice(1)}` : 'All Products'}
          </h1>
          <p className="text-gray-600">
            {filteredProducts.length} products found
          </p>
        </div>

        <div className="flex flex-col lg:flex-row gap-8">
          {/* Filters Sidebar */}
          <div className="lg:w-1/4">
            <div className="bg-gray-50 p-6 rounded-lg sticky top-4">
              <h3 className="text-lg font-medium text-black mb-4">Filters</h3>
              
              {/* Search */}
              <div className="mb-6">
                <label className="block text-sm font-medium text-gray-700 mb-2">Search</label>
                <input
                  type="text"
                  placeholder="Search products..."
                  value={filters.search}
                  onChange={(e) => handleFilterChange('search', e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-black focus:border-transparent"
                />
              </div>

              {/* Category Filter */}
              <div className="mb-6">
                <label className="block text-sm font-medium text-gray-700 mb-2">Category</label>
                <select
                  value={filters.category}
                  onChange={(e) => handleFilterChange('category', e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-black focus:border-transparent"
                >
                  <option value="">All Categories</option>
                  <option value="women">Women</option>
                  <option value="men">Men</option>
                  <option value="kid">Kids</option>
                  <option value="shoes">Shoes</option>
                </select>
              </div>

              {/* Shoe Type Filter - Only show when shoes category is selected */}
              {filters.category === 'shoes' && (
                <div className="mb-6">
                  <label className="block text-sm font-medium text-gray-700 mb-2">Shoe Type</label>
                  <select
                    value={filters.shoeType || ''}
                    onChange={(e) => handleFilterChange('shoeType', e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-black focus:border-transparent"
                  >
                    <option value="">All Shoe Types</option>
                    <option value="running">Running Shoes</option>
                    <option value="casual">Casual Sneakers</option>
                    <option value="training">Training Shoes</option>
                    <option value="walking">Walking Shoes</option>
                    <option value="basketball">Basketball Shoes</option>
                    <option value="fashion">Fashion Sneakers</option>
                  </select>
                </div>
              )}

              {/* Price Range Filter */}
              <div className="mb-6">
                <label className="block text-sm font-medium text-gray-700 mb-2">Price Range</label>
                <select
                  value={filters.priceRange}
                  onChange={(e) => handleFilterChange('priceRange', e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-black focus:border-transparent"
                >
                  <option value="all">All Prices</option>
                  <option value="under50">Under $50</option>
                  <option value="50to100">$50 - $100</option>
                  <option value="100to200">$100 - $200</option>
                  <option value="over200">Over $200</option>
                </select>
              </div>

              {/* Clear Filters */}
              <button
                onClick={() => setFilters({ category: '', filter: '', search: '', priceRange: 'all' })}
                className="w-full text-sm text-gray-600 hover:text-black transition-colors"
              >
                Clear All Filters
              </button>
            </div>
          </div>

          {/* Products Grid */}
          <div className="lg:w-3/4">
            {filteredProducts.length === 0 ? (
              <div className="text-center py-12">
                <p className="text-gray-600 text-lg mb-4">No products found matching your criteria.</p>
                <button
                  onClick={() => setFilters({ category: '', filter: '', search: '', priceRange: 'all' })}
                  className="btn-primary"
                >
                  Clear Filters
                </button>
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
                {filteredProducts.map((product) => (
                  <div key={product.id} className="product-card group">
                    <Link to={`/products/${product.id}`}>
                      <div className="aspect-square bg-gray-200 mb-4 flex items-center justify-center overflow-hidden rounded-lg">
                        <img 
                          src={product.image} 
                          alt={product.name}
                          className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-300"
                          onError={(e) => {
                            e.target.style.display = 'none';
                            e.target.nextSibling.style.display = 'flex';
                          }}
                        />
                        <div className="hidden w-full h-full items-center justify-center text-gray-500">
                          {product.name}
                        </div>
                      </div>
                      <div className="p-2">
                        <h3 className="text-lg font-medium text-black mb-2 group-hover:text-gray-700 transition-colors">
                          {product.name}
                        </h3>
                        <p className="text-gray-600 mb-2 capitalize">{product.category}</p>
                        <div className="flex items-center justify-between">
                          <p className="text-xl font-semibold text-black">${product.price}</p>
                          {product.tags.includes('sale') && (
                            <span className="bg-red-100 text-red-600 px-2 py-1 text-xs rounded-full">SALE</span>
                          )}
                          {product.tags.includes('new') && (
                            <span className="bg-green-100 text-green-600 px-2 py-1 text-xs rounded-full">NEW</span>
                          )}
                        </div>
                      </div>
                    </Link>
                    <div className="p-2 pt-0">
                      <button className="w-full btn-primary text-sm py-2">Add to Cart</button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Products;
